import { PlusOutlined } from '@ant-design/icons';
import { Card, Input } from 'antd';
import React, { useState } from 'react';
import PostCard from '../Components/PostCard';


const Home = () => {

  const [show, setShow] = useState(true)
  let [showCommentBox, setShowCommentBox] = useState([])

  return (
    <>
      {
        [1, 2, 3, 4, 5, 6, 7, 7, 99, 6, 4, 2].map((itm, idx) => (
          <>
            <PostCard key={idx} />

            {show && <Card bordered={false} style={{ width: 500 }}>
              <Input placeholder="Basic usage" key={Math.random()} suffix={<PlusOutlined onClick={() => {
                let temp = [...showCommentBox]
                temp[idx] = !!temp[idx]
                setShowCommentBox(temp)
              }} />} />
              <p>Card content</p>
              <p>Card content</p>
            </Card>}
          </>
        ))
      }
    </>
  );
}

export default Home;
