import { message } from 'antd';
import React, { useEffect, useState } from 'react';
import { client } from '../apolloGqlClient';
import PostCard from '../Components/PostCard';
import { fetch_posts_query } from '../gqlQueries';

const Home = () => {

  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)

  return (
    posts.map((itm, idx) => <PostCard key={itm.id} content={itm.body} loading={loading} />)
  );
}

export async function getStaticProps() {

  console.log('clled server side')

  try {
    const resp = await client.query({
      query: fetch_posts_query, fetchPolicy: 'no-cache', variables: {
        input: null
      }
    })
    console.log(resp)
    resp?.data?.fetchPosts ? setPosts(resp?.data?.fetchPosts) : message.error('something went wrong!')

  } catch (error) { console.log(error); message.error('Could not fetch posts') }

  return {
    props: {
      posts: resp?.data?.fetchPosts
    },
  }
}

export default Home;
