import { EditOutlined, MessageOutlined, SettingOutlined, HeartFilled, PlusOutlined } from '@ant-design/icons';
import { Avatar, Card, Skeleton, Input } from 'antd';
import { gql } from '@apollo/client';
import React, { useEffect, useState } from 'react';
import { client } from '../gqlQueries';
const { Meta } = Card;



const PostCard = () => {

    const [show, setShow] = useState(false)

    const commentBox = () => (
        <Card bordered={false} style={{ width: 500 }}>
            <Input placeholder="Basic usage" key={Math.random()} suffix={<PlusOutlined />} style={{ marginBottom: 15 }} />
            <p>Card content</p>
            <p>Card content</p>
        </Card>
    )

    let data = gql`
    query fetchPosts(input:null) {
       id
       body
       user_id
       # likes{
       #   liker_name
       #   liker_id
       #   liker_email
       # }
       # comments{
       #   comment
       #   is_deleted
       #   commentator_name
       # }
   }`


    useEffect(async () => {
        const resp = await client.query({ query: data, fetchPolicy: 'no-cache' })
        console.log(resp)


    }, [])

    return (
        <>
            <Card
                style={{ width: 500, marginTop: 16 }}
                actions={[
                    <SettingOutlined key="setting" />,
                    <EditOutlined key="edit" />,
                    <HeartFilled />,
                    <MessageOutlined onClick={() => setShow(!show)} />
                ]}
            >
                <Skeleton loading={false} avatar active>
                    <Meta
                        avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                        title="Card title"
                        description="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. "
                    />
                </Skeleton>
            </Card>

            {show && commentBox}
        </>
    );
}

export default PostCard;
